package com.class03;

public class EvenOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n;
		n=21;
				
		if(n==0) {
			System.out.println("Null");
		}	else if (n%2==0){
			System.out.println("Number is even");
			}	else {
				System.out.println("Number is odd");
			}
	}

}
