package com.class06;

public class NotOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean rain=true;
		if(!rain) {
			System.out.println("I'll go to the park");
		}
		
		boolean traffic=false;
		if(!traffic) {
			System.out.println("I'll be on time");
		}
	}

}
